<?php

namespace DatabaseBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * PlayerDataRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class PlayerDataRepository extends EntityRepository
{
    public function getCategory($id, $season)
    {
       $query = $this->createQueryBuilder('players')
                ->join('players.playerPerson', 'playerperson')
                ->join('playerperson.personalData', 'personaldata')
                ->join('players.season', 'season')
                ->where('personaldata.id = :id')
                ->andWhere('season.id = :seasonId')
                ->setParameter('id', $id)
                ->setParameter('seasonId', $season)
                ->getQuery();
        return $query->getOneOrNullResult();
    }


    public function getByCategory($name, $seasonId, $page)
    {
        $query = $this->createQueryBuilder('players')
            ->join('players.playerPerson', 'playerperson')
            ->join('playerperson.personalData', 'personaldata')
            ->join('players.season', 'season')
            ->where('players.category LIKE :category')
            ->andWhere('season.id = :seasonId')
            ->setParameter('category', $name)
            ->setParameter('seasonId', $seasonId)
            ->orderBy('personaldata.surname')
            ->getQuery();
        $paginator = \DatabaseBundle\Repository\PaginatorRepository::paginate($query, $page);
        return array('paginator' => $paginator,
                    'query' => $query);
    }

    public function countByCategory($category, $seasonId) {
        $query = $this->createQueryBuilder('players')
                    ->select('count(players)')
                    ->join('players.season', 'season')
                    ->join('players.playerPerson', 'playerperson')
                    ->where('players.category LIKE :category')
                    ->andWhere('season.id = :seasonId')
                    ->andWhere('playerperson.isPlayer = 1')
                    ->setParameter('category', $category)
                    ->setParameter('seasonId', $seasonId)
                    ->getQuery();
        return $query->getSingleScalarResult();
    }

    public function deleteFromTeam($id, $seasonId) {
        $query = $this->createQueryBuilder('players')
            ->join('players.playerPerson', 'playerperson')
            ->join('playerperson.personalData', 'personaldata')
            ->join('players.season', 'season')
            ->where('personaldata.id = :id')
            ->andWhere('season.id = :seasonId')
            ->setParameter('id', $id)
            ->setParameter('seasonId', $seasonId)
            ->getQuery();
        return $query
            ->getOneOrNullResult();
    }
}
?>
